import React, { Component } from 'react';
import { ShipContext } from '../../contexts/ShipProvider'
import axios from 'axios';


export default class ShipControl extends Component {
    state = {
        ship: null,
        stage: null,
        celestialBody: null
    }

    async componentDidMount() {
        if (this.props.location.id) {
            try {
                const shiptRes = await axios({
                    method: 'GET',
                    url: `http://localhost:1337/ships/${this.props.location.id}`
                });
                const fetchedShip = shiptRes.data
                let rocketStages = []
                for (let i = 0; i < fetchedShip.stage.length; i++) {
                    let stage = {
                        commandModule: [],
                        engine: [],
                        tank: [],
                    }
                    let currentStage = i
                    for (let i = 0; i < fetchedShip.stage[currentStage].tank.length; i++) {
                        stage.tank.push(fetchedShip.stage[currentStage].tank[i].tank)
                    }
                    for (let i = 0; i < fetchedShip.stage[currentStage].engine.length; i++) {
                        stage.engine.push(fetchedShip.stage[currentStage].engine[i].engine)
                    }
                    for (let i = 0; i < fetchedShip.stage[currentStage].commandModule.length; i++) {
                        stage.commandModule.push(fetchedShip.stage[currentStage].commandModule[i].commandModule)
                    }
                    currentStage++
                    rocketStages.push(stage)
                }
                console.log('rocketStages', rocketStages);
                this.setState({
                    stage: rocketStages,
                    ship: fetchedShip
                }, () => {
                    this.context.shipSetStage(rocketStages)
                })
                console.log('fetchedShip', fetchedShip)
            } catch (err) {
                alert(err);
            }
            console.log(this.state.ship, this.state.stage)
        } else {
            
        }
    }

    fromSurfaceToOrbit = () => {
        let stage = this.context.state.stage
        let requierdDV = Math.sqrt(this.state.ship.celest_body.mu / this.state.ship.celest_body.radius) * 1000 * this.state.ship.celest_body.atmosphere
        let dvByStageAfterBurn = [...this.context.state.deltaVByStage]
        let dvbystage = [...this.context.state.deltaVByStage]
        let dif = []

        // Minus deltaV
        for (let i = 0; i < dvByStageAfterBurn.length; i++) {
            if (dvByStageAfterBurn[i] > requierdDV) {
                dvByStageAfterBurn[i] -= requierdDV
                requierdDV = 0
                console.log('Congratulation, you reach orbit')
            } else {
                requierdDV -= dvByStageAfterBurn[i]
                dvByStageAfterBurn[i] = 0
            }
        }
        if (requierdDV !== 0) {
            console.log('you failed')
            return
        }
        console.log('dvbystage', dvbystage);
        console.log('dvByStageAfterBurn', dvByStageAfterBurn);
        // stage separation
        for (let i = 0; i < dvByStageAfterBurn.length; i++) {
            if (dvByStageAfterBurn[i] === 0) {
                stage.splice(0, 1)
                dvByStageAfterBurn.splice(0, 1)
                dvbystage.splice(0, 1)
            }
        }
        // getDv % diference
        for (let i = 0; i < dvbystage.length; i++) {
            dif.push(dvByStageAfterBurn[i] / (dvbystage[i] / 100))
        }
        console.log('dif', dif);
        // updateFuelLastStage
        for (let i = 0; i < stage[0].tank.length; i++) {
            stage[0].tank[i].remainingFuel = (stage[0].tank[i].remainingFuel / 100 * dif[0]) / 1.5
        }
        //stage update
        this.context.shipSetStage(stage)
        this.context.updateLocation('orbit')

    }

    render() {

        return (
            <div className="shipControl">
                <button onClick={() => this.fromSurfaceToOrbit()}>go to orbit :</button>
            </div>
        );
    }
}

ShipControl.contextType = ShipContext;
