import React, { Component } from 'react';
import { ShipContext } from '../../contexts/ShipProvider'
import { updateShip } from '../../utils/updateShip'
import { deleteShip } from '../../utils/deleteShip'
import FromSurface from './FromSurface'
import FromOrbit from './FromOrbit'
import axios from 'axios';

export default class ManoeuvreControl extends Component {
    state={
        celestBodies: []
    }
    async componentDidMount() {
        if(this.context.state.ship.celest_body){
        try {
            const celestBodyRes = await axios({
                method: 'GET',
                url: `http://localhost:1337/celest-bodies/`
            })
            const fetchedCelestBody = celestBodyRes.data
            this.setState({celestBodies: fetchedCelestBody})
            console.log('fetchedCelestBody', fetchedCelestBody);
        } catch(e){
            alert(e)
        }}
    }

    fromSurfaceToOrbit = () => {
        let stage = this.context.state.stage
        let requierdDV = Math.sqrt(this.context.state.ship.celest_body.mu / this.context.state.ship.celest_body.radius) * 1000 * this.context.state.ship.celest_body.atmosphere
        let dvByStageAfterBurn = [...this.context.state.deltaVByStage]
        let dvbystage = [...this.context.state.deltaVByStage]
        let dif = []
        console.log('reqDV', requierdDV);
        // Minus deltaV
        for (let i = 0; i < dvByStageAfterBurn.length; i++) {
            if (dvByStageAfterBurn[i] > requierdDV) {
                dvByStageAfterBurn[i] -= requierdDV
                requierdDV = 0
                console.log('Congratulation, you reach orbit')
            } else {
                requierdDV -= dvByStageAfterBurn[i]
                dvByStageAfterBurn[i] = 0
            }
        }
        if (requierdDV !== 0) {
            console.log('you crashed')
            deleteShip(this.context.state.ship.id)
            return
        }
        console.log('dvbystage', dvbystage);
        console.log('dvByStageAfterBurn', dvByStageAfterBurn);
        // stage separation
        for (let i = 0; i < dvByStageAfterBurn.length; i++) {
            if (dvByStageAfterBurn[i] === 0) {
                stage.splice(0, 1)
                dvByStageAfterBurn.splice(0, 1)
                dvbystage.splice(0, 1)
            }
        }
        // getDv % diference
        for (let i = 0; i < dvbystage.length; i++) {
            dif.push(dvByStageAfterBurn[i] / (dvbystage[i] / 100))
        }
        console.log('dif', dif);
        // updateFuelLastStage
        for (let i = 0; i < stage[0].tank.length; i++) {
            stage[0].tank[i].remainingFuel = (stage[0].tank[i].remainingFuel / 100 * dif[0]) / 1.6
        }
        //stage update
        this.context.shipSetStage(stage)
        this.context.updateLocation('orbit')
        updateShip(stage, this.context.state.ship.name, 'orbit', this.context.state.ship.celest_body.id, this.context.state.ship.id, this.context.state.ship.celest_body.lowOrbit)
        console.log('manoeuvre context ', this.context);
    }

    burn = (requierdDV, risk) => {
        let stage = this.context.state.stage
        let dvByStageAfterBurn = [...this.context.state.deltaVByStage]
        let dvbystage = [...this.context.state.deltaVByStage]
        let dif = []

        for (let i = 0; i < dvByStageAfterBurn.length; i++) {
            if (dvByStageAfterBurn[i] > requierdDV) {
                dvByStageAfterBurn[i] -= requierdDV
                requierdDV = 0
            } else {
                requierdDV -= dvByStageAfterBurn[i]
                dvByStageAfterBurn[i] = 0
            }
        }
        console.log('dvbystage', dvbystage);
        console.log('dvByStageAfterBurn', dvByStageAfterBurn);
        // stage separation
        for (let i = 0; i < dvByStageAfterBurn.length; i++) {
            if (dvByStageAfterBurn[i] === 0) {
                stage.splice(0, 1)
                dvByStageAfterBurn.splice(0, 1)
                dvbystage.splice(0, 1)
            }
        }
        // getDv % diference
        for (let i = 0; i < dvbystage.length; i++) {
            dif.push(dvByStageAfterBurn[i] / (dvbystage[i] / 100))
        }
        console.log('dif', dif);
        // updateFuelLastStage
        for (let i = 0; i < stage[0].tank.length; i++) {
            stage[0].tank[i].remainingFuel = (stage[0].tank[i].remainingFuel / 100 * dif[0]) / 1.6
        }
        if (requierdDV !== 0) {
            if(risk === true){
                deleteShip(this.context.state.ship.id)
                return false
            }
        }
        return stage
    }

    fromOrbitToSurface = () => {
        let requierdDV = Math.sqrt(this.context.state.ship.celest_body.mu / this.context.state.ship.celest_body.radius) * 1000 / this.context.state.ship.celest_body.atmosphere
        let stage = this.burn(requierdDV, true)
        if(stage === false){
            console.log('you crashed');
        } else {
            console.log('congrats, you landed');
        }
        //stage update
        this.context.shipSetStage(stage)
        this.context.updateLocation('ground')
        updateShip(stage, this.context.state.ship.name, 'ground', this.context.state.ship.celest_body.id, this.context.state.ship.id, 0)
        console.log('manoeuvre context ', this.context);
    }

    commandModuleReEntry = () => {
        let requierdDV = Math.sqrt(this.context.state.ship.celest_body.mu / this.context.state.ship.celest_body.radius) * 100 
        console.log(requierdDV)
    }

    async getCelestBody(id){
        try {
            const celestBodyRes = await axios({
                method: 'GET',
                url: `http://localhost:1337/celest-bodies/${id}`
            })
            const fetchedCelestBody = celestBodyRes.data
            return  fetchedCelestBody.apoapsis
        } catch(e){
            alert(e)
        }
    }
    
     escapeFromOrbit = async () => {
        let requierdDV = this.context.state.ship.celest_body.escapeVelocity
        console.log(requierdDV)
        let stage = this.context.state.stage
        let dvByStageAfterBurn = [...this.context.state.deltaVByStage]
        let dvbystage = [...this.context.state.deltaVByStage]
        let dif = []
        console.log('reqDV', requierdDV);

        const altitude = await this.getCelestBody(this.context.state.ship.celest_body.id)
        console.log(altitude);

        // Minus deltaV
        for (let i = 0; i < dvByStageAfterBurn.length; i++) {
            if (dvByStageAfterBurn[i] > requierdDV) {
                dvByStageAfterBurn[i] -= requierdDV
                requierdDV = 0
                console.log('you are now orbiting around the sun')
            } else {
                requierdDV -= dvByStageAfterBurn[i]
                dvByStageAfterBurn[i] = 0
            }
        }
        if (requierdDV !== 0) {
            console.log('no more fuel')
            return
        }
        console.log('dvbystage', dvbystage);
        console.log('dvByStageAfterBurn', dvByStageAfterBurn);
        // stage separation
        for (let i = 0; i < dvByStageAfterBurn.length; i++) {
            if (dvByStageAfterBurn[i] === 0) {
                stage.splice(0, 1)
                dvByStageAfterBurn.splice(0, 1)
                dvbystage.splice(0, 1)
            }
        }
        // getDv % diference
        for (let i = 0; i < dvbystage.length; i++) {
            dif.push(dvByStageAfterBurn[i] / (dvbystage[i] / 100))
        }
        console.log('dif', dif);
        // updateFuelLastStage
        for (let i = 0; i < stage[0].tank.length; i++) {
            stage[0].tank[i].remainingFuel = (stage[0].tank[i].remainingFuel / 100 * dif[0]) / 1.6
        }
        //stage update
        this.context.shipSetStage(stage)
        this.context.updateLocation('orbit')
        updateShip(stage, this.context.state.ship.name, 'orbit', 3 , this.context.state.ship.id, this.context.state.ship.celest_body.apoapsis, altitude)
        console.log('manoeuvre context ', this.context);
    }

    childTransfer = () => {
        let requierdDV
        console.log(requierdDV)
    }

    planetTransfert = (targetBodyPeriapsis) => {
        let requierdDV = parseInt(Math.sqrt( this.context.state.ship.celest_body.mu / this.context.state.ship.altitudeFromParent) * (Math.sqrt(Math.pow(2 * targetBodyPeriapsis, 2) / (this.context.state.ship.altitudeFromParent + targetBodyPeriapsis) -1 )))
        console.log(requierdDV);
    }

    render() {
            if (this.context.state.ship.locationStatus === 'orbit') {
                return <ShipContext.Provider>
                        <FromOrbit
                            fromOrbitToSurface={this.fromOrbitToSurface}
                            escapeFromOrbit={this.escapeFromOrbit}
                            childTransfer={this.childTransfer}
                            commandModuleReEntry={this.commandModuleReEntry}
                            planetTransfert={(targetBodyPeriapsis) => this.planetTransfert(targetBodyPeriapsis)}
                            celestBodiesList={this.state.celestBodies}
                        />
                    </ShipContext.Provider>
            } else if (this.context.state.ship.locationStatus === 'ground') {
                return <ShipContext.Provider>
                        <FromSurface
                            fromSurfaceToOrbit={this.fromSurfaceToOrbit}
                        />
                     </ShipContext.Provider>
            }
        else {
            return ''
        }
    }
}

ManoeuvreControl.contextType = ShipContext;
