import React, { Component } from 'react';

export default class FromOrbit extends Component {
    handleChange = (event) => {
        setCelestBody(event.target.value);  
    }
    render() {
        return (
            <div>
            <button
                // hasGround={props.hasGround}
                onClick={() => props.fromOrbitToSurface()}>
                land on ground
            </button>
            <button
                // isPlanet={props.hasChild}
                onClick={() => props.escapeFromOrbit()}>
                escape gravity
        </button>
            <button
                // hasChild={props.hasChild}
                onClick={() => props.childTransfer()}>
                tranfert to child
        </button>
            <button
                //  hasParachute={props.hasParachute && hasAtmospher}
                onClick={() => props.commandModuleReEntry()}>
                Make a re-entry burn and land the capsule
        </button>
            <form>
                <select value={celestBody} onChange={(e)=>handleChange(e)}>
                    {props.celestBodiesList.map((e, i) => {
                        if (e.type === 'planet') {
                            return (
                                <option key={i} value={e} > {e.name}  </option>
                            )
                        } else {
                            return ''
                        }
                    }
                    )}
                </select>
                <button onClick={() => props.planetTransfert()}> tranfert </button>
            </form>
        </div>
        );
    }
}
