import React, { Component } from 'react';
import { ShipContext } from '../../contexts/ShipProvider'
import axios from 'axios';


export default class ShipControl extends Component {
    state ={
        ship: null,
        stage: null,
        celestialBody: null
    }

    async componentDidMount() {
        try {
            const shiptRes = await axios({
                method: 'GET',
                url: `http://localhost:1337/ships/${this.props.location.id}`
            });
            const fetchedShip = shiptRes.data
            let rocketStages = []
            for (let i = 0; i < fetchedShip.stage.length; i++) {
                let stage = {
                    commandModule: [],
                    engine: [],
                    tank: [],
                }
                let currentStage = i
                for (let i = 0; i < fetchedShip.stage[currentStage].tank.length; i++) {
                    stage.tank.push(fetchedShip.stage[currentStage].tank[i].tank)
                }
                for (let i = 0; i < fetchedShip.stage[currentStage].engine.length; i++) {
                    stage.engine.push(fetchedShip.stage[currentStage].engine[i].engine)
                }
                for (let i = 0; i < fetchedShip.stage[currentStage].commandModule.length; i++) {
                    stage.commandModule.push(fetchedShip.stage[currentStage].commandModule[i].commandModule)
                }
                currentStage++
                rocketStages.push(stage)
            }
            console.log('rocketStages', rocketStages);
            this.setState({ 
                stage: rocketStages,
                ship: fetchedShip
            }, () => {
                this.context.shipSetStage(rocketStages)
            })
            console.log('fetchedShip', fetchedShip)
        } catch (err){
            alert(err);
        }
        console.log(this.state.ship, this.state.stage);
    }

    fromSurfaceToOrbit = () => {
        let stage = this.state.stage
        let requierdDV = Math.sqrt(this.state.ship.celest_body.mu/this.state.ship.celest_body.radius) * 1000 * this.state.ship.celest_body.atmosphere

        setInterval(() => {
            stage[0].tank[0].remainingFuel -= 200
            console.log('remainingFuel', stage[0].tank[0].remainingFuel);
        }, 300);
    }

    render() {

        return (
            <div className="shipControl">
                <button onClick={() => this.fromSurfaceToOrbit()}>go to orbit :</button>
            </div>
        );
    }
}

ShipControl.contextType = ShipContext;
